var Footnote = function (link){

  var self = this;
  var $link = $(link);

  var $note = $(link.href.match(/(#.*)/)[1]);

  var idx = link.href.match(/(\d+)$/)[1];

  var start = $link.position();

  var inner = $("<li>")
    .addClass("footnote-container")
    .html($note.html())
    .find(".returner")
      .attr("title", "Hide this footnote")
      .click(function (ev){
        self.hide("fast");
        return false;
      })
    .end();

  this.element = $("<ol>")
    .hide()
    .attr({ start: idx })
    .addClass("footnote-popup")
    .html(inner)
    .appendTo($link.closest("p"));

  var end = { left: 15 };

  this.show = function (){
    if (this.visible()){
      this.element.effect("shake", { distance: 5, times: 3 }, 40)
    } else {
      this.element
        .css({ left: start.left + "px", top: start.top + "px", opacity: 0, display: "block" })
        .animate({ left: end.left + "px", opacity: 1 }, Footnote.animation_params(lay_dem_bitches_out));
    }
  };

  this.hide = function (){
    if (this.visible()){
      this.element.fadeOut("fast");
    }
  };

  this.visible = function (){
    return this.element.is(":visible");
  };

  function lay_dem_bitches_out(){
    Footnote.all(function (idx){
      console.log('idx = ' + idx);
      if (idx == 0) return;

      var self = this.footnote;
      if (!self.visible()) return

      var prev = Footnote.notes[idx - 1].footnote;
      if (!prev.visible()) return;

      var target = prev.element.position().top + prev.element.outerHeight();
      if (self.element.position().top < target){
        self.element.animate({ top: target + "px" }, Footnote.animation_params());
      }
    });
  }
};

Footnote.notes = [];

Footnote.animation_params = function (callback){
  return { duration: 350, easing: "easeOutCubic", complete: callback };
}

Footnote.all = function (callback){
  return $.each(Footnote.notes, callback);
}

Footnote.add = function (link){
  Footnote.notes.push({ href: link.href, footnote: new Footnote(link) });
};

Footnote.show = function (link){
  Footnote.all(function (){
    if (this.href == link.href){
      this.footnote.show();
    }
  });
};

Footnote.hideAll = function (){
  Footnote.all(function (){ this.footnote.hide(); });
};

;(function ($){
  $.fn.footnote = function (){

    if (navigator.userAgent.indexOf("MSIE") >= 0) return;

    $(document.body).click(function (ev){
      if (!$(ev.target).closest("li").andSelf().hasClass("footnote-container")){
        Footnote.hideAll();
      }
    });

    //$("footer").hide();

    return $(this).each(function (){
      Footnote.add(this);
    }).click(function (ev){
      Footnote.show(this);
      return false;
    });
  };
})(jQuery);

